datasource.query:
			WITH
				MEMBER [Measures].[IncomeAccum] AS
  					SUM( { [Time].[All].FirstChild.FirstChild.FirstChild : [Time].[Month].CurrentMember }, [Measures].[Income] )
			 	MEMBER [Measures].[OutcomeAccum] AS
   					SUM( { [Time].[All].FirstChild.FirstChild.FirstChild : [Time].[Month].CurrentMember }, [Measures].[Outcome] )
			 	MEMBER [Measures].[Balance] AS
   					[Measures].[IncomeAccum] - [Measures].[OutcomeAccum] 

	    	SELECT {
	    		[Measures].[IncomeAccum], [Measures].[OutcomeAccum], [Measures].[Balance]
	    	} ON COLUMNS, 
	    	{
	    		Descendants(${year.default([Time].[All].LastChild)}, [Time].[Month])
	    	} on rows
	    	FROM [CashFlow]
	    	WHERE ${subProject.parent(project).parent(customer)}
