datasource.query:
    	SELECT {[Measures].[Effort]} ON COLUMNS, 
    	NON EMPTY {
    		TopCount(
    			ORDER(
    				{
    					Descendants(${customer.default([Project].[All])}, [Project].[Project])
					},
    				([Measures].[Effort]),
    				DESC
    			),
    			10
    		)
    	} on rows
    	FROM [Effort]
    	WHERE ${month.parent(year).default([Time].[All].LastChild)}, ${profile.parent(employee)}, ${seniority}, ${workingHours}
